import { useContextGlobal } from "./utils/global.context";
import { useState } from "react";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
  const {theme, handleTheme}= useContextGlobal()

  const emailValidation = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;

  const [contact, setContact] = useState({
    name: '',
    email: ''
  });


  const [Info, setInfo] = useState(false);

  const [Error, setError] = useState(false);

  const upDateName = (e) => {

    setContact({...contact, [e.target.name]: e.target.value });
  }
  const handleSubmit = (e) => {

    e.preventDefault();

    if(contact.name.length > 5 && contact.email.match(emailValidation)) {
      setInfo(true);
      setError(false);
      console.log(contact);
    } else {
      setInfo(false);
      setError(true)
    }
  }

  return (
    <div onSubmit={handleSubmit} className="form">
      <form>
        <input type="text" placeholder="Ingrese su Nombre Completo"  value={name} onChange= {upDateName} required="required"></input>
        <input type="mail" placeholder="Ingrese su email" value={mail} onChange= {handleSubmit}  id= "mail" required="required"></input>
        <button type="submit" >Send</button>
      </form>

      {Info && <p>{`Gracias ${contact.name}, Nos pondremos en contacto con usted por correo electrónico`}</p>}
      {Error && <p style={{color: "yellow"}}>Por favor, verifica lu información.</p>}
    </div>
  );
};

export default Form;
