import React from 'react'
import axios from 'axios'
import { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { useContextGlobal } from '../Components/utils/global.context'



const Detail = () => {

  const { state, dispatch }= useContextGlobal();
  const params = useParams()
  const urlID = `https://jsonplaceholder.typicode.com/users/${params.id}`

  useEffect(() => {
    axios(urlID).then(response => {
      console.log(response);
      dispatch ({type: 'ONE_DENTIST', payload: response.data})
      state.theme == 'light' ? "light main" : "dark main"
    })
  })

  return (
    <div className={state.theme == 'light' ? "light main" : "dark main"}>
      <h1>Detalle del DR {state.dentistDetail.surname}</h1>
      <img src="/images/doctor.jpg" alt="doctor" />
        <h4>{state.dentistDetail.name} {state.dentistDetail.surname}</h4>
        <p>Email: {state.dentistDetail.email}</p>
        <p>Phone Number: {state.dentistDetail.phone}</p>
        <p><strong>Website: </strong>{state.dentistDetail.website}</p>
    </div>
  )
}

export default Detail
